typedef struct {
  GTypeInterface g_iface;

  /* Virtual Table */

  gpointer   (*get_user_data)      (GAsyncResult                *async_result);
  GObject *  (*get_source_object)  (GAsyncResult                *async_result);
} GAsyncResultIface;
